import {
  Card,
  CardContent,
  Typography,
  CardActions,
  Button,
} from "@mui/material";
import { useState } from "react";
import backendUrl from "../config";
import axios from "axios";
import { useEffect } from "react";
import TextField from "@mui/material/TextField";
import Popper from "@mui/material/Popper";

function Post(props) {
  const [userInfo, setUserInfo] = useState([]);
  const [commentText, setCommentText] = useState("");
  const [comments, setComments] = useState([]);
  const [anchorEl, setAnchorEl] = useState(null);

  const handleViewComments = (event) => {
    setAnchorEl(anchorEl ? null : event.currentTarget);
  };
  const open = Boolean(anchorEl);
  const id = open ? "simple-popper" : undefined;

  const handleCommentText = (event) => {
    const inputValue = event.target.value;
    setCommentText(inputValue);
  };
  const handleNewComment = async () => {
    const currentTimestamp = new Date().toISOString();
    axios
      .post(`${backendUrl}api/newComment`, {
        post_id: props.content.post_id,
        user_id: props.userInfo.user_id,
        content: commentText,
        created_at: currentTimestamp,
      })
      .then((response) => {
        const { message } = response.data;
        console.log("Response from server:", message);
        //  props.getUserPosts();
        setCommentText("");
      })
      .catch((error) => {
        console.error("Error posting data: ", error);
      });
  };
  const getComments = async () => {
    axios
      .get(`${backendUrl}api/getComments/${props.content.post_id}`)
      .then((response) => {
        const message = response.data.rows;
        setComments(message);
      })
      .catch((error) => {
        console.error("Error fetching data: ", error);
      });
  };

  const findByUserID = async () => {
    axios
      .get(`${backendUrl}api/findByUserID/${props.content.user_id}`)
      .then((response) => {
        const message = response.data;
        setUserInfo(message);
      })
      .catch((error) => {
        console.error("Error fetching data: ", error);
      });
  };
  useEffect(() => {
    findByUserID();
    getComments();
  }, []);
  return (
    <Card style={{ maxWidth: "100%" }}>
      <CardContent>
        {
          <Typography gutterBottom variant="h5" component="div">
            {userInfo.first_name} {userInfo.last_name}
          </Typography>
        }
        <Typography variant="body1" color="text.secondary">
          {props.content.content}
        </Typography>
        <TriggerButton
          aria-describedby={id}
          type="button"
          onClick={handleViewComments}
        >
          View comments ({comments.length})
        </TriggerButton>
        <Popper id={id} open={open} anchorEl={anchorEl}>
          <StyledPopperDiv>The content of the Popper.</StyledPopperDiv>
        </Popper>
      </CardContent>
      <CardActions>
        <TextField variant="filled" fullWidth onChange={handleCommentText} />
        <Button size="small" onClick={handleNewComment}>
          Comment
        </Button>
        <Button size="small">Like</Button>
      </CardActions>
    </Card>
  );
}
export default Post;

const blue = {
  50: "#F0F7FF",
  100: "#C2E0FF",
  200: "#99CCF3",
  300: "#66B2FF",
  400: "#3399FF",
  500: "#007FFF",
  600: "#0072E5",
  700: "#0059B2",
  800: "#004C99",
  900: "#003A75",
};

const grey = {
  50: "#f6f8fa",
  100: "#eaeef2",
  200: "#d0d7de",
  300: "#afb8c1",
  400: "#8c959f",
  500: "#6e7781",
  600: "#57606a",
  700: "#424a53",
  800: "#32383f",
  900: "#24292f",
};

const TriggerButton = styled("button")(
  ({ theme }) => `
    font-family: IBM Plex Sans, sans-serif;
    font-size: 0.875rem;
    font-weight: 600;
    box-sizing: border-box;
    border-radius: 8px;
    padding: 8px 16px;
    line-height: 1.5;
    background: transparent;
    cursor: pointer;
    border: 1px solid ${theme.palette.mode === "dark" ? grey[800] : grey[200]};
    color: ${theme.palette.mode === "dark" ? blue[300] : blue[500]};

    &:hover {
      background: ${theme.palette.mode === "dark" ? grey[900] : grey[100]};
      border-color: ${theme.palette.mode === "dark" ? blue[200] : blue[400]};
    }

    &:focus-visible {
      border-color: ${blue[400]};
      outline: 3px solid ${
        theme.palette.mode === "dark" ? blue[500] : blue[200]
      };
    }
    `
);

const StyledPopperDiv = styled("div")(
  ({ theme }) => css`
    background-color: ${theme.palette.mode === "dark" ? grey[900] : grey[50]};
    border-radius: 8px;
    border: 1px solid ${theme.palette.mode === "dark" ? grey[700] : grey[200]};
    box-shadow: ${theme.palette.mode === "dark"
      ? `0px 4px 8px rgb(0 0 0 / 0.7)`
      : `0px 4px 8px rgb(0 0 0 / 0.1)`};
    padding: 0.75rem;
    color: ${theme.palette.mode === "dark" ? blue[200] : blue[800]};
    font-size: 0.875rem;
    font-family: "IBM Plex Sans", sans-serif;
    font-weight: 500;
    opacity: 1;
    margin: 0.25rem 0;
  `
);
